function learndash_video_iframe_shortcode($atts) { if (empty($atts['link'])) { return '<strong>Error:</strong> No video link provided.'; } $video_url = esc_url($atts['link']); return ' <div style="margin: 0; padding: 0; width: 100%; height: 100%; background: transparent;"> <iframe src="' . $video_url . '" style=" display: block; width: 100vw; height: 66.25vw; /* 16:9 ratio */ max-width: 100%; max-height: 100%; border: none; margin: 0; padding: 0; " allow="accelerometer; gyroscope; autoplay; encrypted-media; picture-in-picture" allowfullscreen loading="lazy"> </iframe> </div>'; } add_shortcode("ld_video", "learndash_video_iframe_shortcode");